(()=>{"use strict";(e=>{const t=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),s=()=>{const e=(new Date).getTime(),t=(new Date("24 april 2022").getTime()-e)/1e3;return{remainingTime:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}};let m=s(),i=setInterval((()=>{m.remainingTime>0?(t.textContent=m.hours<10?"0"+m.hours:m.hours,n.textContent=m.minutes<10?"0"+m.minutes:m.minutes,o.textContent=m.seconds<10?"0"+m.seconds:m.seconds):(t.textContent="00",n.textContent="00",o.textContent="00"),m=s(),m.remainingTime<0&&clearInterval(i)}),500)})(),console.log("второй модуль")})();